/*UC Only Setup*/
/*ACC Branding*/
h1, h2, h3, h4, h5, h6, p, td, th {
  -webkit-transition: line-height 1s;
  /* Safari */
  transition: line-height 1s;
  -webkit-transition: margin 1s;
  /* Safari */
  transition: margin 1s;
}

.sub-line {
  display: block;
  font-size: 50%;
  clear: both;
  line-height: 1.25em;
  margin-top: -0.25em;
  opacity: 0.75;
}

.table .thead,
.table thead {
  background-color: #fff;
}
.table .thead th,
.table .thead td,
.table thead th,
.table thead td {
  border-bottom: 2px solid #1faed7;
}
.table .tfoot,
.table tfoot {
  background-color: #f0f0f1;
}
.table .tfoot th,
.table .tfoot td,
.table tfoot th,
.table tfoot td {
  border-top: 1px solid #7c8089;
  font-style: italic;
}

.table.column-shaded .thead th:nth-child(even),
.table.column-shaded thead th:nth-child(even) {
  background-color: #d7ecff;
}
.table.column-shaded .tbody td:nth-child(even),
.table.column-shaded tbody td:nth-child(even) {
  background-color: #e4f6fb;
}
.table.column-shaded .tbody tr:nth-child(even) td:nth-child(even),
.table.column-shaded tbody tr:nth-child(even) td:nth-child(even) {
  background-color: #a6d5ff;
}

.table.unshaded {
  width: auto;
  border: none;
}
.table.unshaded thead {
  border-top: none;
  border-right: none;
  border-left: none;
}
.table.unshaded tbody tr:nth-child(even) {
  background-color: unset;
}
.table.unshaded tbody tr + tr td {
  border-top: 1px #f0f0f1 solid;
}
.table.unshaded tfoot {
  background-color: unset;
}

i.icon-complete_check-circle:before, .is-complete i.icon-complete_check:before, i.icon-toggle_arrow-circle:before, i.icon-toggle_caret-circle-right-down:before, i.icon-toggle_chevron-double-left-right:before, i.icon-toggle_chevron-double-up-down:before, i.icon-toggle_chevron-up-down:before, i.icon-toggle_plus-minus:before, i.icon-toggle_bars-times:before {
