.lds-grid {
  display: grid;
  grid-template-columns: 16px 16px 16px;
  grid-gap: 12px;
  position: relative;
  width: 64px;
  height: 64px;
  margin: 0 auto;
}

.lds-grid .dot {
  width: 16px;
  height: 16px;
  animation: lds-grid 1.5s linear infinite;
}

.lds-grid .dot:nth-child(1) {
  animation-delay: 0s;
}

.lds-grid .dot:nth-child(2) {
  animation-delay: -0.3s;
}

.lds-grid .dot:nth-child(4) {
  animation-delay: -0.3s;
}

.lds-grid .dot:nth-child(3) {
  animation-delay: -0.6s;
}

.lds-grid .dot:nth-child(5) {
  animation-delay: -0.6s;
}

.lds-grid .dot:nth-child(7) {
  animation-delay: -0.6s;
}

.lds-grid .dot:nth-child(6) {
  animation-delay: -0.9s;
}

.lds-grid .dot:nth-child(8) {
  animation-delay: -0.9s;
}

.lds-grid .dot:nth-child(9) {
  animation-delay: -1.2s;
}

@keyframes lds-grid {
  0%, 100% {
    opacity: 0.95;
  }
  10%, 90% {
    opacity: 0.8;
  }
  50% {
    opacity: 0.25;
  }
}

.lorem-text {
  height: 1.4em;
}

.lorem-image {
  width: 300px;
  height: 200px;
}

.lorem-thumb {
  width: 100px;
  height: 200px;
}

.lorem-loader {
  color: #fff;
  background: linear-gradient(-45deg, #92dbef, #c1c3c7, #dad6d1, #8cf0da);
  background-size: 400% 400%;
  -webkit-animation: Gradient 8s ease infinite;
  -moz-animation: Gradient 8s ease infinite;
  animation: Gradient 8s ease infinite;
}

@-webkit-keyframes Gradient {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}
@-moz-keyframes Gradient {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}
@keyframes Gradient {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

.exapnded-click-area::after {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1;
  pointer-events: auto;
  content: "";
  background-color: #0000;
}

.transition-all_1s {
  transition-duration: 1s;
  transition-timing-function: ease;
  transition-property: all;
}

