.counter_reset {
  counter-reset: a;
}

.counter_increment {
  counter-increment: a;
}

.counter:before {
  content: counter(a);
}

.counter_lower-alpha:before {
  content: counter(a, lower-alpha);
}

.counter_upper-alpha:before {
  content: counter(a, upper-alpha);
}

.counter_lower-roman:before {
  content: counter(a, lower-roman);
}

.counter_upper-roman:before {
  content: counter(a, upper-roman);
}

.counter-sub_reset {
  counter-reset: b;
}

.counter-sub_increment {
  counter-increment: b;
}

.counter-sub:before {
  content: counter(b);
}

.counter-sub_lower-alpha:before {
  content: counter(b, lower-alpha);
}

.counter-sub_upper-alpha:before {
  content: counter(b, upper-alpha);
}

.counter-sub_lower-roman:before {
  content: counter(a, lower-roman);
}

.counter-sub_upper-roman:before {
  content: counter(a, upper-roman);
}

.order-marker {
  border-radius: 50%;
  display: inline-block;
  height: 1.5em;
  width: 1.5em;
  margin-right: 0;
  margin-bottom: 0;
  padding: 0.75em 0 0 0;
  position: relative;
  margin-right: 0.5em;
  text-align: center;
  box-sizing: border-box;
  line-height: 0;
  vertical-align: top;
}

a:focus {
  color: unset;
}

*[class^=" hover:"],
*[class^=" h:"] {
  transition: all ease-out 0.75s;
  cursor: pointer;
}
*[class^=" hover:"]:focus,
*[class^=" h:"]:focus {
  background: inherit;
  border: inherit;
  color: inherit;
}

*[class^="hover:c"],
*[class^=" h:c"] {
  transition: all ease-in 0.5s;
}

