sections.css:
.button {
  white-space: nowrap;
}
.button.primary {
  background-color: #24929d;
  color: white;
}
.button.primary:hover, .button.primary:focus {
  background-color: #1d757e;
  color: white;
}
.button.secondary {
  background-color: #63646b;
  color: white;
}
.button.secondary:hover, .button.secondary:focus {
  background-color: #4f5056;
  color: white;
}
.button.success {
  background-color: #3b9b19;
  color: white;
}
.button.success:hover, .button.success:focus {
  background-color: #2f7c14;
  color: white;
}
.button.warning {
  background-color: #e18725;
  color: white;
}
.button.warning:hover, .button.warning:focus {
  background-color: #b86c19;
  color: white;
}
.button.alert {
  background-color: #cb241c;
  color: white;
}
.button.alert:hover, .button.alert:focus {
  background-color: #a21d16;
  color: white;
}
.button.teal {
  background-color: #137680;
  color: white;
}
.button.teal:hover, .button.teal:focus {
  background-color: #0f5e66;
  color: white;
}
.button.grey {
  background-color: #808589;
  color: white;
}
.button.grey:hover, .button.grey:focus {
  background-color: #666a6e;
  color: white;
}
.button.blue {
  background-color: #4f74bf;
  color: white;
}
.button.blue:hover, .button.blue:focus {
  background-color: #3a5b9e;
  color: white;
}
.button.green {
  background-color: #509f6c;
  color: white;
}
.button.green:hover, .button.green:focus {
  background-color: #407f56;
  color: white;
}
.button.purple {
  background-color: #735dba;
  color: white;
}
.button.purple:hover, .button.purple:focus {
  background-color: #58439d;
  color: white;
}
.button.magenta {
  background-color: #ba5e93;
  color: white;
}
.button.magenta:hover, .button.magenta:focus {
  background-color: #9d4377;
  color: white;
}
.button.shadow {
  box-shadow: 1.5px 1.5px 6px 0.75px rgba(0, 0, 0, 0.4);
  transition: box-shadow ease-out 0.25s;
}
.button.shadow:hover {
  box-shadow: 0.5px 0.5px 2px 0.25px rgba(0, 0, 0, 0.1);
}

.close-button {
  top: 0;
  right: 0;
  left: 0;
  width: 100%;
  text-align: right;
  border-top: #24929d 9px solid;
}
.close-button span {
  background: #24929d;
  color: white;
  padding: 0 0.2em;
  border: 0;
  top: 0px;
  line-height: 13px;
  font-size: 26px;
  vertical-align: top;
}
.close-button:focus {
  outline: none;
}

.border_dotted {
  border-style: dotted;
}

