.button{ 
	white-space: nowrap;
	 //  @each $name, $color in $button-palette {
		// &.#{$name} {
	 //  		@include button-style($color, auto, white);
	 //  	}
	 //  }
	&.hollow{
		background-color: transparent;
	}
	&.clear{
		border-color: transparent;
	}
	&.shadow{
		@extend %hover_transition !optional;
		box-shadow: make-shadow(3,"drop",#000,.4);
		 
		&:hover{
			box-shadow: make-shadow(1,"drop",#000,.1);
		}
	}
}

.quicklinks{
	a:hover{
		background-color: get-themecolor(primary, 5);
		color:$black;
	}
}

.quicklinks__item{
	border-right:1px solid get-themecolor(primary, 1);
	padding:0 2px;
	&:last-child{border-right-width:0;}
} 
.quicklinks__item .quicklinks__link{
	@include get-sized-style(padding-top, positive, 2, 1rem, "" );
	@include get-sized-style(padding-bottom, positive, 2, 1rem, "" );
	@extend .font_n1;
	&:last-child{
		border-right:0;
	}
}

.pagination-container{
    background: get-themecolor(primary, 0);
    padding: $padding_0 $padding_3;
    .pagination{ margin-bottom: $margin_3}
    .current{
        background:$white;
        color: get-themecolor(primary, 0);
    }
    a:not([class="pagination-previous"]):not([class="pagination-next"]){
    	color:$white;
    	&:hover{
    		color:$black;
    	}
    }
}

