.border_dotted,.border_dashed,.border_solid,.border_none{border-width:0;}
.border_dotted { border-style: dotted; }
.border_dashed { border-style: dashed; }
.border_solid {  border-style: solid; }
.border_none {   border-style: none; }

@media #{$medium-up} {
  .border_dotted-ns { border-style: dotted; }
  .border_dashed-ns { border-style: dashed; }
  .border_solid-ns {  border-style: solid; }
  .border_none-ns {   border-style: none; }
}

@media #{$medium-only} {
  .border_dotted-m { border-style: dotted; }
  .border_dashed-m { border-style: dashed; }
  .border_solid-m {  border-style: solid; }
  .border_none-m {   border-style: none; }
}

@media #{$large-up} {
  .border_dotted-l { border-style: dotted; }
  .border_dashed-l { border-style: dashed; }
  .border_solid-l {  border-style: solid; }
  .border_none-l {   border-style: none; }
}

 
 
