@include size-loop(border-radius, border-radius,1);
@include size-loop(border-top-right-radius, border-radius,1);
@include size-loop(border-top-left-radius, border-radius,1);
@include size-loop(border-bottom-right-radius, border-radius,1);
@include size-loop(border-bottom-left-radius, border-radius,1);

@each  $val-name, $val in  map-get($values,border-radius){
  .#{$val-name}{
      &,.switch-paddle,.switch-paddle::after{
        border-radius: $val;
      }
  }
}

@include size-loop(border-width, positive,.5rem);
@include size-loop(border-top-width, positive,.5rem);
@include size-loop(border-right-width, positive,.5rem);
@include size-loop(border-left-width, positive,.5rem);
@include size-loop(border-bottom-width, positive,.5rem);

%hover_transition,*[class^="hover_"],*[class^=" hover_"]{
  transition: all ease-out .75s;
}


@each $color-name, $color in $acc-palette{
    @include hover-loop(hover-bg, $color-name, $color);
    @include hover-loop(hover-text, $color-name, $color);

}

%hover_transition-in{
  transition: all ease-in .5s;
}

.hover_color_white.hover_color_black,.hover_black,.hover_white{
  @extend %hover_transition-in;

}
.hover_color_white:hover,.hover_white:hover{
  color:white;
}
.hover_color_black:hover,.hover_black:hover{
  color:black;
}

.hover_underline{
  text-decoration: none;
  &:hover{text-decoration: underline;}
}

